"use strict";const itemsPokemon=document.querySelector(".items"),blockPopup=document.querySelector(".pop__up");function buildItems(e){e.forEach(e=>{var t=document.createElement("p");t.addEventListener("click",clickPokemon),t.className="name__pokemon",t.innerText=e.name,itemsPokemon.append(t)})}function clickPokemon(e){e=e.target.innerText;fetch("https://pokeapi.co/api/v2/pokemon/"+e,{method:"GET"}).then(e=>e.json()).then(e=>addPopup(e))}function addPopup(e){var t=blockPopup.children.length;for(let e=0;e<t;e++)blockPopup.lastElementChild.remove();const n=document.createElement("div");var o=document.createElement("button"),p=document.createElement("img");o.addEventListener("click",()=>{n.style.display="none"}),o.innerText="Закрити",n.innerHTML=`<p>Покемон: ${e.name}</p>
    <p>Зріст покемона: ${e.height}</p>
    <p>Вага покемона: ${e.weight}</p>`,n.className="pop",p.src=e.sprites.front_default,n.append(p),blockPopup.append(n),n.append(o)}fetch("https://pokeapi.co/api/v2/pokemon",{method:"GET"}).then(e=>e.json()).then(e=>buildItems(e.results));